mapper.select:
select count(postIdx) from tbl_post

    postState=1 and postCategory = #{postCategory}

        and ${search_type} like concat("%", #{search_word}, "%")


        and date(postCreatedAt) >= #{search_date1}


        and date(postCreatedAt) <= #{search_date2}

select postIdx, postTitle, postContent, postMemberId, postCategory, postReadCnt, postCreatedAt, postUpdatedAt, postState
from tbl_post

    postState=1 and postCategory = #{postCategory}

        and ${search_type} like concat("%", #{search_word}, "%")


        and date(postCreatedAt) >= #{search_date1}


        and date(postCreatedAt) <= #{search_date2}


order by postCreatedAt desc
limit #{page_skip_count}, #{page_size}

mapper.update:
update tbl_post
set
    postTitle = #{postTitle},
    postContent = #{postContent},
    postUpdatedAt = #{postUpdatedAt}
where postIdx = #{postIdx}

update tbl_post
set postState = 2
where postIdx = #{postIdx}
