mapper.update:
update tbl_post_comment
set postCommentContent = #{postCommentContent}
where postCommentIdx = #{postCommentIdx}

mapper.select:
select postCommentIdx, postCommentRefPostIdx, postCommentParentIdx, postCommentMemberId, postCommentContent, postCommentCreatedAt, postCommentUpdatedAt, postCommentState
from tbl_post_comment
where postCommentRefPostIdx = #{postCommentRefPostIdx}
