mapper.select:
select count(memberIdx)
from tbl_member
where memberId = #{memberId}
  and memberPwd = #{memberPwd}

select memberIdx
from tbl_member
where memberId = #{memberId}

mapper.update:
update tbl_member
set memberState = #{memberState}
    where memberId = #{memberId}

update tbl_member
set memberPwdChangeDate = #{memberPwdChangeDate}
    where memberId = #{memberId}

update tbl_member
set memberLastLogin = #{memberLastLogin}
    where memberId = #{memberId}

