mapper.select:
select count(postLikeIdx)
from tbl_post_like AS l
JOIN tbl_post AS p on l.postLikeRefIdx = p.postIdx
where postLikeMemberId = #{postLikeMemberId}
and postLikeRefType = 'POST'



            AND postTitle LIKE CONCAT('%', #{searchDTO.searchWord}, '%')


            AND postContent LIKE CONCAT('%', #{searchDTO.searchWord}, '%')


            AND postMemberId LIKE CONCAT('%', #{searchDTO.searchWord}, '%')






            AND postLikeCreatedAt BETWEEN #{searchDTO.startDate} AND #{searchDTO.endDate}


            AND postCreatedAt BETWEEN #{searchDTO.startDate} AND #{searchDTO.endDate}

SELECT reportIdx, reportRefIdx, reportMemberId, reportCreatedAt, reportState, postTitle, postMemberId, postCreatedAt, postContent FROM tbl_report AS r JOIN tbl_post AS p ON r.reportRefIdx = p.postIdx WHERE reportMemberId = #{reportMemberId}
AND postTitle LIKE CONCAT('%', #{searchDTO.searchWord}, '%')
AND postContent LIKE CONCAT('%', #{searchDTO.searchWord}, '%')
AND postMemberId LIKE CONCAT('%', #{searchDTO.searchWord}, '%')
AND reportCreatedAt BETWEEN #{searchDTO.startDate} AND #{searchDTO.endDate}
AND postCreatedAt BETWEEN #{searchDTO.startDate} AND #{searchDTO.endDate}
ORDER BY
postTitle postMemberId postCreatedAt postCreatedAt
ASC DESC
LIMIT #{pageDTO.pageSkipCount}, #{pageDTO.pageSize}

select count(reportIdx)
from tbl_report AS r
JOIN tbl_post AS p on r.reportRefIdx = p.postIdx
where reportMemberId = #{reportMemberId}



            AND postTitle LIKE CONCAT('%', #{searchDTO.searchWord}, '%')


            AND postContent LIKE CONCAT('%', #{searchDTO.searchWord}, '%')


            AND postMemberId LIKE CONCAT('%', #{searchDTO.searchWord}, '%')






            AND reportCreatedAt BETWEEN #{searchDTO.startDate} AND #{searchDTO.endDate}


            AND postCreatedAt BETWEEN #{searchDTO.startDate} AND #{searchDTO.endDate}

SELECT o.orderIdx, orderMemberId, orderAmount, orderStatus, orderCreatedAt, orderUpdatedAt, lectureIdx, lectureTitle, lectureDescription, lectureTeacherId, lectureCreatedAt, lectureThumbnailImg, lectureAmount FROM tbl_order_info AS o JOIN tbl_order_lecture AS ol ON o.orderIdx = ol.orderIdx JOIN tbl_lecture AS l ON ol.orderLectureIdx = l.lectureIdx WHERE orderMemberId = #{orderMemberId} AND orderStatus = 's'
AND lectureTitle LIKE CONCAT('%', #{searchDTO.searchWord}, '%')
AND lectureTeacherId LIKE CONCAT('%', #{searchDTO.searchWord}, '%')
AND lectureDescription LIKE CONCAT('%', #{searchDTO.searchWord}, '%')
AND orderCreatedAt BETWEEN #{searchDTO.startDate} AND #{searchDTO.endDate}
AND lectureCreatedAt BETWEEN #{searchDTO.startDate} AND #{searchDTO.endDate}
ORDER BY
orderAmount orderCreatedAt orderCreatedAt
ASC DESC
LIMIT #{pageDTO.pageSkipCount}, #{pageDTO.pageSize}

SELECT COUNT(DISTINCT o.orderIdx)
FROM tbl_order_info o
JOIN tbl_order_lecture ol ON o.orderIdx = ol.orderIdx
JOIN tbl_lecture l ON ol.orderLectureIdx = l.lectureIdx
WHERE o.orderMemberId = #{orderMemberId}




            AND l.lectureTitle LIKE CONCAT('%', #{searchDTO.searchWord}, '%')


            AND l.lectureDescription LIKE CONCAT('%', #{searchDTO.searchWord}, '%')


            AND l.lectureTeacherId LIKE CONCAT('%', #{searchDTO.searchWord}, '%')







            AND o.orderCreatedAt BETWEEN #{searchDTO.startDate} AND #{searchDTO.endDate}


            AND l.lectureCreatedAt BETWEEN #{searchDTO.startDate} AND #{searchDTO.endDate}

SELECT DISTINCT o.orderIdx FROM tbl_order_info o JOIN tbl_order_lecture ol ON o.orderIdx = ol.orderIdx JOIN tbl_lecture l ON ol.orderLectureIdx = l.lectureIdx WHERE o.orderMemberId = #{orderMemberId}
AND l.lectureTitle LIKE CONCAT('%', #{searchDTO.searchWord}, '%')
AND l.lectureDescription LIKE CONCAT('%', #{searchDTO.searchWord}, '%')
AND l.lectureTeacherId LIKE CONCAT('%', #{searchDTO.searchWord}, '%')
AND o.orderCreatedAt BETWEEN #{searchDTO.startDate} AND #{searchDTO.endDate}
AND l.lectureCreatedAt BETWEEN #{searchDTO.startDate} AND #{searchDTO.endDate}
ORDER BY
o.orderAmount o.orderCreatedAt o.orderCreatedAt
ASC DESC
LIMIT #{pageDTO.pageSkipCount}, #{pageDTO.pageSize}

SELECT o.orderIdx, o.orderMemberId, o.orderAmount, o.orderStatus, o.orderCreatedAt, o.orderUpdatedAt, l.lectureIdx, l.lectureTitle, l.lectureDescription, l.lectureTeacherId, l.lectureCreatedAt, l.lectureThumbnailImg, l.lectureAmount FROM tbl_order_info o JOIN tbl_order_lecture ol ON o.orderIdx = ol.orderIdx JOIN tbl_lecture l ON ol.orderLectureIdx = l.lectureIdx WHERE o.orderIdx IN
#{idx}
AND l.lectureTitle LIKE CONCAT('%', #{searchDTO.searchWord}, '%')
AND l.lectureDescription LIKE CONCAT('%', #{searchDTO.searchWord}, '%')
AND l.lectureTeacherId LIKE CONCAT('%', #{searchDTO.searchWord}, '%')
AND o.orderCreatedAt BETWEEN #{searchDTO.startDate} AND #{searchDTO.endDate}
AND l.lectureCreatedAt BETWEEN #{searchDTO.startDate} AND #{searchDTO.endDate}
ORDER BY
o.orderAmount o.orderCreatedAt o.orderCreatedAt
ASC DESC

mapper.update:
UPDATE tbl_order_info
SET orderStatus    = #{orderStatus},
    orderUpdatedAt = NOW()
WHERE orderIdx = #{orderIdx}
  AND orderMemberId = #{memberId}
  AND orderStatus = 's'

mapper.select:
select b.bookmarkIdx, b.bookmarkLectureIdx, b.bookmarkMemberId, b.bookmarkState, b.bookmarkCreatedAt, l.lectureTitle, l.lectureThumbnailImg, l.lectureAmount, t.teacherName, l.lectureCreatedAt from tbl_bookmark b join tbl_lecture l on b.bookmarkLectureIdx = l.lectureIdx join tbl_teacher t on l.lectureTeacherId = t.teacherId where b.bookmarkMemberId = #{bookmarkMemberId}
and l.lectureTitle like concat('%', #{searchDTO.searchWord}, '%')
and t.teacherName like concat('%', #{searchDTO.searchWord}, '%')
and l.lectureCreatedAt between #{searchDTO.startDate} and #{searchDTO.endDate}
and bookmarkCreatedAt between #{searchDTO.startDate} and #{searchDTO.endDate}
order by
l.lectureTitle t.teacherName l.lectureCreatedAt b.bookmarkCreatedAt bookmarkCreatedAt
asc desc
limit #{pageDTO.pageSkipCount}, #{pageDTO.pageSize}

select count(bookmarkIdx)
from tbl_bookmark b
join tbl_lecture l on b.bookmarkLectureIdx = l.lectureIdx
join tbl_teacher t on l.lectureTeacherId = t.teacherId
where b.bookmarkMemberId = #{bookmarkMemberId}




            and l.lectureTitle like concat('%', #{searchDTO.searchWord}, '%')


            and t.teacherName like concat('%', #{searchDTO.searchWord}, '%')







            and l.lectureCreatedAt between #{searchDTO.startDate} and #{searchDTO.endDate}


            and bookmarkCreatedAt between #{searchDTO.startDate} and #{searchDTO.endDate}

SELECT DISTINCT p.postIdx, p.postTitle, p.postContent, p.postMemberId, p.postCategory, p.postReadCnt, p.postCreatedAt, p.postUpdatedAt, p.postState, r.reportState FROM tbl_post AS p LEFT JOIN tbl_report AS r ON p.postIdx = r.reportRefIdx WHERE p.postMemberId = #{postMemberId}
AND p.postTitle LIKE CONCAT('%', #{searchDTO.searchWord}, '%')
AND p.postContent LIKE CONCAT('%', #{searchDTO.searchWord}, '%')
AND p.postCreatedAt BETWEEN #{searchDTO.startDate} AND #{searchDTO.endDate}
AND p.postUpdatedAt BETWEEN #{searchDTO.startDate} AND #{searchDTO.endDate}
ORDER BY
p.postIdx p.postTitle p.postContent p.postCreatedAt p.postIdx
ASC DESC
LIMIT #{pageDTO.pageSkipCount}, #{pageDTO.pageSize}

SELECT COUNT(DISTINCT p.postIdx)
FROM tbl_post AS p
LEFT JOIN tbl_report AS r ON p.postIdx = r.reportRefIdx
WHERE p.postMemberId = #{postMemberId}




            AND p.postTitle LIKE CONCAT('%', #{searchDTO.searchWord}, '%')


            AND p.postContent LIKE CONCAT('%', #{searchDTO.searchWord}, '%')







            AND p.postCreatedAt BETWEEN #{searchDTO.startDate} AND #{searchDTO.endDate}


            AND p.postUpdatedAt BETWEEN #{searchDTO.startDate} AND #{searchDTO.endDate}

select postCommentIdx, postCommentRefPostIdx, postCommentParentIdx, postCommentMemberId, postCommentContent, postCommentCreatedAt, postCommentUpdatedAt, postCommentState from tbl_post_comment where postCommentMemberId = #{postCommentMemberId}
and postCommentContent like concat('%', #{searchDTO.searchWord}, '%')
and postCommentCreatedAt between #{searchDTO.startDate} and #{searchDTO.endDate}
and postCommentUpdatedAt between #{searchDTO.startDate} and #{searchDTO.endDate}
order by
postCommentIdx postCommentContent postCommentCreatedAt postCommentIdx
asc desc
limit #{pageDTO.pageSkipCount}, #{pageDTO.pageSize}

select count(postCommentIdx)
from tbl_post_comment
where postCommentMemberId = #{postCommentMemberId}




            and postCommentContent like concat('%', #{searchDTO.searchWord}, '%')







            and postCommentCreatedAt between #{searchDTO.startDate} and #{searchDTO.endDate}


