mapper.select:
select count(*)
from tbl_cart
where cartMemberId = #{cartMemberId}
and cartLectureIdx = #{cartLectureIdx}

mapper.insert:
insert into tbl_order_lecture (orderIdx,orderLectureIdx)
VALUES (
        (select MAX(orderIdx) from tbl_order_info where 1=1),
        #{orderLectureIdx}
       )

INSERT INTO tbl_payment
