,#{paymentTransactionId}
,#{paymentApprovedAt2}
,null
,now()
,now()
);

mapper.delete:
delete from tbl_cart where 1=1
and cartMemberId = #{memberId}
and cartLectureIdx in (select orderLectureIdx from tbl_order_lecture where orderIdx = #{paymentOrderIdx})

mapper.select:
