mapper.delete:
delete from tbl_post_like
where postLikeRefIdx = #{postLikeRefIdx}
  and postLikeRefType= #{postLikeRefType}
  and postLikeMemberId=#{postLikeMemberId}

mapper.select:
select postLikeIdx, postLikeRefIdx, postLikeRefType, postLikeMemberId, postLikeCreatedAt, postTitle, postMemberId, postCreatedAt, postContent from tbl_post_like AS l JOIN tbl_post AS p on l.postLikeRefIdx = p.postIdx where postLikeMemberId = #{postLikeMemberId} and postLikeRefType = 'POST'
AND postTitle LIKE CONCAT('%', #{searchDTO.searchWord}, '%')
AND postContent LIKE CONCAT('%', #{searchDTO.searchWord}, '%')
AND postMemberId LIKE CONCAT('%', #{searchDTO.searchWord}, '%')
AND postLikeCreatedAt between #{searchDTO.startDate} AND #{searchDTO.endDate}
limit #{pageDTO.pageSkipCount} , #{pageDTO.pageSize}
