mapper.select:
select teacherIdx, teacherName, teacherId, teacherIntro, teacherApproval, teacherProfileImg, teacherSubject
from tbl_teacher
where teacherId = #{teacherId}

select lectureTitle, lectureDescription
from tbl_lecture
where lectureIdx = #{lectureIdx}

select teacherId, teacherName, teacherProfileImg, teacherSubject
from tbl_teacher
where teacherSubject = #{subject}

mapper.delete:
delete from tbl_bookmark
where bookmarkLectureIdx = #{lectureIdx}
and bookmarkMemberId = #{memberId}

mapper.select:
select a.bookmarkLectureIdx
from tbl_bookmark a
join tbl_lecture b on a.bookmarkLectureIdx = b.lectureIdx
where b.lectureTeacherId = #{teacherId}
and a.bookmarkMemberId = #{memberId}
